VALUES
	(1, 'subscriptionemails_paid', 0, 'Your [LEVEL] subscription at [SITENAME] is now enabled', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>The payment for your [LEVEL] subscription on our site has just been cleared. Your subscription is now activated and will remain active until [PUBLISH_DOWN] GMT.</p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 1, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(2, 'subscriptionemails_new_active', 0, 'Your new [LEVEL] subscription at [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your new <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription is now activated and will remain active until [PUBLISH_DOWN] GMT.</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 2, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(3, 'subscriptionemails_new_renewal', 0, 'Your [LEVEL] subscription renewal at [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription has been renewed. The renewed subscription will be activated on [PUBLISH_UP] GMT and remain active until [PUBLISH_DOWN] GMT.</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 3, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(4, 'subscriptionemails_new_pending', 0, 'Your new [LEVEL] subscription at [SITENAME] is being processed', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>The payment for your new or renewed <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription is currently being processed. When the payment is cleared we will send you a new email. </span><span style=\"line-height: 1.3em;\">Please note that on some occasions it may take 2 to 7 business days for the payment to be cleared. This time period is outside of our control and is mainly due to restrictions imposed by your bank or the payment processor company.</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 4, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(5, 'subscriptionemails_cancelled_new', 0, 'Your new [LEVEL] subscription at [SITENAME] could not be processed', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>The payment processor declined the payment for your new or renewed <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription</span><span style=\"line-height: 1.3em;\">. Please revisit our site and retry subscribing using the same username and email address. You may want to select a different means of payment, for example a different credit card. We apologise for the inconvenience</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you to tried to subscribe to <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 5, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(6, 'subscriptionemails_cancelled_existing', 0, 'Your [LEVEL] subscription at [SITENAME] was cancelled', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>The payment for your <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription was cancelled by our payment processor or refunded at your request</span><span style=\"line-height: 1.3em;\">.</span></p>\r\n<p><span style=\"line-height: 1.3em;\">If you believe you are receiving this message in error and would like to remain subscribed to our site, please visit our site, log in with your username and password and retry subscribing. You may want to select a different means of payment, for example a different credit card. We apologise for the inconvenience</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you had a subscription at <em>[SITENAME]</em>. Do not reply to this email, it\'s sent from an unmonitored email address.</p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 6, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(7, 'subscriptionemails_expired', 0, 'Your [LEVEL] subscription at [SITENAME] has expired', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription on our site has expired</span><span style=\"line-height: 1.3em;\">. If you want to renew it </span><a href=\"[MYSUBSURL]\" style=\"line-height: 1.3em;\">please visit the subscriptions status page on our site</a> and click on the \"Renew\" button to the right of the expired subscription.</p>\r\n<p><span style=\"line-height: 1.3em;\"><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you had a subscription at <em>[SITENAME]</em>. Do not reply to this email, it\'s sent from an unmonitored email address.</p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 7, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(8, 'subscriptionemails_published', 0, 'Your renewed [LEVEL] subscription at [SITENAME] has been activated', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription renewal on our site has been activated</span><span style=\"line-height: 1.3em;\">. It will remain active until [PUBLISH_DOWN] GMT.</span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n<p>You can <a href=\"[MYSUBSURL]\">review the status of all your subscriptions</a> any time on our site.</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription at <em>[SITENAME]</em>. Do not reply to this email, it\'s sent from an unmonitored email address.</p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 8, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(9, 'subscriptionemails_generic', 0, 'Your [LEVEL] subscription at [SITENAME] has been updated', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your <span style=\"line-height: 1.3em;\">[LEVEL] </span><span style=\"line-height: 1.3em;\">subscription on our site has been modified. Its new details are:</span></p>\r\n<table width=\"100%\" border=\"0\" cellspacing=\"2\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td width=\"25%\"><strong>Username</strong></td>\r\n<td>[USERNAME] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Subscription type</strong></td>\r\n<td>[LEVEL] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Subscription status</strong></td>\r\n<td>[ENABLED] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Payment status</strong></td>\r\n<td>[PAYSTATE] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Valid From</strong></td>\r\n<td>[PUBLISH_UP] GMT</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Valid To</strong></td>\r\n<td>[PUBLISH_DOWN] GMT </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span style=\"line-height: 1.3em;\"><span style=\"line-height: 1.3em;\">You can </span><a href=\"[MYSUBSURL]\" style=\"line-height: 1.3em;\">review the status of all your subscriptions</a><span style=\"line-height: 1.3em;\"> any time on our site.</span></span></p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription at <em>[SITENAME]</em>. Do not reply to this email, it\'s sent from an unmonitored email address.</p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 9, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
	(10, 'asexpirationnotify_first', 0, 'Your [LEVEL] subscription on [SITENAME] will expire soon', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello [FIRSTNAME],</p>\r\n<p>Your [LEVEL] subscription with the following details is going to expire soon:</p>\r\n<table width=\"100%\" border=\"0\" cellspacing=\"2\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td width=\"25%\"><strong>Username</strong></td>\r\n<td>[USERNAME] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Subscription type</strong></td>\r\n<td>[LEVEL] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Subscription status</strong></td>\r\n<td>[ENABLED] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Payment status</strong></td>\r\n<td>[PAYSTATE] </td>\r\n</tr>\r\n<tr>\r\n<td><strong>Valid From</strong></td>\r\n<td>[PUBLISH_UP] GMT</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Valid To</strong></td>\r\n<td>[PUBLISH_DOWN] GMT </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span style=\"line-height: 1.3em;\"><span style=\"line-height: 1.3em;\"><span style=\"line-height: 1.3em;\">You can login any time on our site to </span><a href=\"[MYSUBSURL]\" style=\"line-height: 1.3em;\">review the status of all your subscriptions</a><span style=\"line-height: 1.3em;\"> and renew your expiring one.</span></span></span></p>\r\n<p><span style=\"line-height: 1.3em;\">We\'d like to remind you that you have registered on our site using the username [USERNAME] and email address [USEREMAIL].</span></p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a subscription in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>\r\n<p style=\"text-align: right; font-size: 7pt; color: #ccc;\">Powered by <a href=\"https://www.akeebabackup.com/products/akeeba-subscriptions.html\" style=\"color: #ccf; text-decoration: none;\">Akeeba Subscriptions</a></p>', '*', 1, 10, '2013-01-23 00:00:00', 42, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00', 0),
